er name=\"magento_customercustomattributes\" instance=\"Magento\\CustomerCustomAttributes\\Observer\\CoreCopyFieldsetSalesCopyOrderToEdit\" />\r\n    </event>\r\n    <event name=\"core_copy_fieldset_sales_copy_order_billing_address_to_order\">\r\n        <observer name=\"magento_customercustomattributes\" instance=\"Magento\\CustomerCustomAttributes\\Observer\\CoreCopyFieldsetSalesCopyOrderBillingAddressToOrder\" />\r\n    </event>\r\n    <event name=\"core_copy_fieldset_sales_copy_order_shipping_address_to_order\">\r\n        <observer name=\"magento_customercustomattributes\" instance=\"Magento\\CustomerCustomAttributes\\Observer\\CoreCopyFieldsetSalesCopyOrderShippingAddressToOrder\" />\r\n    </event>\r\n    <event name=\"core_copy_fieldset_customer_account_to_quote\">\r\n        <observer name=\"magento_customercustomattributes\" instance=\"Magento\\CustomerCustomAttributes\\Observer\\CoreCopyFieldsetCustomerAccountToQuote\" />\r\n    </event>\r\n    <event name=\"core_copy_fieldset_customer_address_to_quote_address\">\r\n        <observer name=\"magento_customercustomattributes\" instance=\"Magento\\CustomerCustomAttributes\\Observer\\CoreCopyFieldsetCustomerAddressToQuoteAddress\" />\r\n    </event>\r\n    <event name=\"core_copy_fieldset_checkout_onepage_quote_to_customer\">\r\n        <observer name=\"magento_customercustomattributes\" instance=\"Magento\\CustomerCustomAttributes\\Observer\\CoreCopyFieldsetCheckoutOnepageQuoteToCustomer\" />\r\n    </event>\r\n    <event name=\"core_copy_fieldset_sales_convert_quote_address_to_customer_address\">\r\n        <observer name=\"magento_customercustomattributes\" instance=\"Magento\\CustomerCustomAttributes\\Observer\\CoreCopyFieldsetQuoteAddressToCustomerAddress\" />\r\n    </event>\r\n    <event name=\"core_copy_fieldset_order_address_to_customer_address\">\r\n        <observer name=\"magento_customercustomattributes\" instance=\"Magento\\CustomerCustomAttributes\\Observer\\CoreCopyFieldsetOrderAddressToCustom