rray\">\r\n                    <item name=\"entityTableName\" xsi:type=\"string\">catalog_product_bundle_selection</item>\r\n                    <item name=\"identifierField\" xsi:type=\"string\">selection_id</item>\r\n                </item>\r\n            </argument>\r\n        </arguments>\r\n    </type>\r\n    <type name=\"Magento\\Framework\\EntityManager\\HydratorPool\">\r\n        <arguments>\r\n            <argument name=\"hydrators\" xsi:type=\"array\">\r\n                <item name=\"Magento\\Bundle\\Api\\Data\\OptionInterface\" xsi:type=\"string\">Magento\\Framework\\EntityManager\\AbstractModelHydrator</item>\r\n                <item name=\"Magento\\Bundle\\Model\\Selection\" xsi:type=\"string\">Magento\\Framework\\EntityManager\\AbstractModelHydrator</item>\r\n            </argument>\r\n        </arguments>\r\n    </type>\r\n\r\n</pre>\r\n\r\n\r\nin extensions_attributes.xml\r\n\r\n<pre lang=\"xml\">\r\n <extension_attributes for=\"Magento\\Catalog\\Api\\Data\\ProductInterface\">\r\n        <attribute code=\"bundle_product_options\" type=\"Magento\\Bundle\\Api\\Data\\OptionInterface[]\" />\r\n    </extension_attributes>\r\n</pre>\r\n\r\n<hr>\r\n<strong>MetaDataPool</strong>\r\n\r\n     * @var \\Magento\\Framework\\EntityManager\\EntityMetadata[]\r\n\r\n\r\nfind this in code\r\n\r\ngetBundleProductOptions\r\n\r\nsetBundleProductOptions\r\n\r\n\r\nProcess of loading an eav model\r\n\r\nadd data from Main table to object\r\n\r\n-> cal EntityManager ->load(\'\' 1, [], )\r\n\r\n\r\nEntityManager --> call TypeResolve --> get Interface which is configure for model\r\n\r\n--> call OperationPool for that Interface--> you can configure OperationPool\r\n\r\ncall operation->execute\r\n\r\n\r\n\r\nIn order to set bundle_product_option and get bundle_product_option \r\n\r\nin di.xml\r\n\r\nMagento configure Pool like this\r\n\r\n\r\n<pre lang=\"php\">\r\n\r\n<type name=\"Magento\\Framework\\EntityManager\\Operation\\ExtensionPool\">\r\n        <arguments>\r\n        