\Adjustment\\Collection\">\r\n    </virtualType>\r\n    <preference for=\"Magento\\Bundle\\Pricing\\Adjustment\\BundleCalculatorInterface\" type=\"Magento\\Bundle\\Pricing\\Adjustment\\Calculator\" />\r\n    <virtualType name=\"Magento\\Bundle\\Pricing\\PriceInfo\" type=\"Magento\\Framework\\Pricing\\PriceInfo\\Base\">\r\n        <arguments>\r\n            <argument name=\"adjustmentCollection\" xsi:type=\"object\">Magento\\Bundle\\Pricing\\Adjustment\\Collection</argument>\r\n        </arguments>\r\n    </virtualType>\r\n    <virtualType name=\"Magento\\Bundle\\Pricing\\Price\\Pool\" type=\"Magento\\Framework\\Pricing\\Price\\Pool\">\r\n        <arguments>\r\n            <argument name=\"prices\" xsi:type=\"array\">\r\n                <item name=\"regular_price\" xsi:type=\"string\">Magento\\Bundle\\Pricing\\Price\\BundleRegularPrice</item>\r\n                <item name=\"final_price\" xsi:type=\"string\">Magento\\Bundle\\Pricing\\Price\\FinalPrice</item>\r\n                <item name=\"tier_price\" xsi:type=\"string\">Magento\\Bundle\\Pricing\\Price\\TierPrice</item>\r\n                <item name=\"special_price\" xsi:type=\"string\">Magento\\Bundle\\Pricing\\Price\\SpecialPrice</item>\r\n                <item name=\"custom_option_price\" xsi:type=\"string\">Magento\\Catalog\\Pricing\\Price\\CustomOptionPrice</item>\r\n                <item name=\"base_price\" xsi:type=\"string\">Magento\\Catalog\\Pricing\\Price\\BasePrice</item>\r\n                <item name=\"configured_price\" xsi:type=\"string\">Magento\\Bundle\\Pricing\\Price\\ConfiguredPrice</item>\r\n                <item name=\"bundle_option\" xsi:type=\"string\">Magento\\Bundle\\Pricing\\Price\\BundleOptionPrice</item>\r\n                <item name=\"catalog_rule_price\" xsi:type=\"string\">Magento\\CatalogRule\\Pricing\\Price\\CatalogRulePrice</item>\r\n            </argument>\r\n        </arguments>\r\n    </virtualType>\r\n    <virtualType name=\"Magento\\Bundle\\Pricing\\Price\\Collection\" type=\"Magento\\Framework\\Pricing\\Price\\Collection\">\r\n        <arguments>\r\n            <argument name=\"pool\" xsi:type=\"object\">Magento\\Bundle\\Pricing\\Price\\Pool</argument>\r\n        </arguments>\r\n    </virtualType>\r\n    <type name=\"Magento\\Framework\\Pricing\\PriceInfo\\Factory\">\r\n        <arguments>\r\n            <argument name=\"types\" xsi:type=\"array\">\r\n                <item name=\"bundle\" xsi:type=\"array\">\r\n                    <item name=\"infoClass\" xsi:type=\"string\">Magento\\Bundle\\Pricing\\PriceInfo</item>\r\n                    <item name=\"prices\" xsi:type=\"string\">Magento\\Bundle\\Pricing\\Price\\Collection</item>\r\n                </item>\r\n            </argument>\r\n        </arguments>\r\n    </type>\r\n    <type name=\"Magento\\Bundle\\Pricing\\Price\\FinalPrice\">\r\n        <arguments>\r\n            <argument name=\"calculator\" xsi:type=\"object\">Magento\\Bundle\\Pricing\\Adjustment\\BundleCalculatorInterface</argument>\r\n        </arguments>\r\n    </type>\r\n    <type name=\"Magento\\Catalog\\Model\\Product\\Attribute\\Backend\\Price\">\r\n        <plugin name=\"bundle\" type=\"Magento\\Bundle\\Model\\Plugin\\PriceBackend\" sortOrder=\"100\" />\r\n    </type>\r\n    <type name=\"Magento\\Catalog\\Model\\Product\">\r\n        <plugin name=\"bundle\" type=\"Magento\\Bundle\\Model\\Plugin\\Product\" sortOrder=\"100\" />\r\n    </type>\r\n    <type name=\"Magento\\Sales\\Model\\Order\\Item\">\r\n        <plugin name=\"bundle\" type=\"Magento\\Bundle\\Model\\Sales\\Order\\Plugin\\Item\" sortOrder=\"100\" />\r\n    </type>\r\n\r\n\r\n</pre>\r\n\r\nMetaPool of EntityManager, what is its role in EAV?\r\n\r\ndi.xml\r\n<pre lang=\"xml\">\r\n    <preference for=\"Magento\\Bundle\\Api\\Data\\OptionInterface\" type=\"Magento\\Bundle\\Model\\Option\" />\r\n <type name=\"Magento\\Framework\\EntityManager\\MetadataPool\">\r\n        <arguments>\r\n            <argument name=\"metadata\" xsi:type=\"array\">\r\n                <item name=\"Magento\\Bundle\\Api\\Data\\OptionInterface\" xsi:type=\"array\">\r\n                    <item name=\"entityTableName\" xsi:type=\"string\">catalog_product_bundle_option</item>\r\n                    <item name=\"identifierField\" xsi:type=\"string\">option_id</item>\r\n                </item>\r\n                <item name=\"Magento\\Bundle\\Model\\Selection\" xsi:type=\"array\">\r\n                    <item name=\"entityTableName\" xsi:type=\"string\">catalog_product_bundle_selection</item>\r\n                    <item name=\"identifierField\" xsi:type=\"string\">selection_id</item>\r\n                </item>\r\n            </argument>\r\n        </arguments>\r\n    </type>\r\n    <type name=\"Magento\\Framework\\EntityManager\\HydratorPool\">\r\n        <arguments>\r\n            <argument name=\"hydrators\" xsi:type=\"array\">\r\n                <item name=\"Magento\\Bundle\\Api\\Data\\OptionInterface\" xsi:type=\"string\">Magento\\Framework\\EntityManager\\AbstractModelHydrator</item>\r\n                <item name=\"Magento\\Bundle\\Model\\Selection\" xsi:type=\"string\">Magento\\Framework\\EntityManager\\AbstractModelHydrator</item>\r\n            </argument>\r\n        </arguments>\r\n    </type>\r\n\r\n</pre>\r\n\r\n\r\nin extensions_attributes.xml\r\n\r\n<pre lang=\"xml\">\r\n <extension_attributes for=\"Magento\\Catalog\\Api\\Data\\ProductInterface\">\r\n        <attribute code=\"bundle_product_options\" type=\"Magento\\Bundle\\Api\\Data\\OptionInterface[]\" />\r\n    </extension_attributes>\r\n</pre>\r\n\r\n<hr>\r\n<strong>MetaDataPool</strong>\r\n\r\n     * @var \\Magento\\Framework\\EntityManager\\EntityMetadata[]\r\n\r\n\r\nfind this in code\r\n\r\ngetBundleProductOptions\r\n\r\nsetBundleProductOptions\r\n\r\n\r\nProcess of loading an eav model\r\n\r\nadd data from Main table to object\r\n\r\n-> cal EntityManager ->load(\'\' 1, [], )\r\n\r\n\r\nEntityManager --> call TypeResolve --> get Interface which is configure for model\r\n\r\n--> call OperationPool for that Interface--> you can configure OperationPool\r\n\r\ncall operation->execute\r\n\r\n\r\n\r\nIn order to set bundle_product_option and get bundle_product_option \r\n\r\nin di.xml\r\n\r\nMagento configure Pool like this\r\n\r\n\r\n<pre lang=\"php\">\r\n\r\n<type name=\"Magento\\Framework\\EntityManager\\Operation\\ExtensionPool\">\r\n        <arguments>\r\n            <argument name=\"extensionActions\" xsi:type=\"array\">\r\n                <item name=\"Magento\\Catalog\\Api\\Data\\ProductInterface\" xsi:type=\"array\">\r\n                    <item name=\"create\" xsi:type=\"array\">\r\n                        <item name=\"create_bundle_options\" xsi:type=\"string\">Magento\\Bundle\\Model\\Product\\SaveHandler</item>\r\n                    </item>\r\n                    <item name=\"update\" xsi:type=\"array\">\r\n                        <item name=\"update_bundle_options\" xsi:type=\"string\">Magento\\Bundle\\Model\\Product\\SaveHandler</item>\r\n                    </item>\r\n                    <item name=\"read\" xsi:type=\"array\">\r\n                        <item name=\"read_bundle_options\" xsi:type=\"string\">Magento\\Bundle\\Model\\Product\\ReadHandler</item>\r\n                    </item>\r\n                </item>\r\n            </argument>\r\n        </arguments>\r\n    </type>\r\n</pre>\r\n\r\n\r\nAbove configuration affect following coding\r\n\r\n\r\n<pre lang=\"php\">\r\n<type name=\"Magento\\Framework\\EntityManager\\Operation\\ExtensionPool\">\r\n        <arguments>\r\n            <argument name=\"extensionActions\" xsi:type=\"array\">\r\n                <item name=\"Magento\\Catalog\\Api\\Data\\ProductInterface\" xsi:type=\"array\">\r\n                    <item name=\"create\" xsi:type=\"array\">\r\n                        <item name=\"create_bundle_options\" xsi:type=\"string\">Magento\\Bundle\\Model\\Product\\SaveHandler</item>\r\n                    </item>\r\n                    <item name=\"update\" xsi:type=\"array\">\r\n                        <item name=\"update_bundle_options\" xsi:type=\"string\">Magento\\Bundle\\Model\\Product\\SaveHandler</item>\r\n                    </item>\r\n                    <item name=\"read\" xsi:type=\"array\">\r\n                        <item name=\"read_bundle_options\" xsi:type=\"string\">Magento\\Bundle\\Model\\Product\\ReadHandler</item>\r\n                    </item>\r\n                </item>\r\n            </argument>\r\n        </arguments>\r\n    </type>\r\n</pre>\r\n\r\n\r\nPHP code\r\nEntityManager.php\r\n\r\n<pre lang=\"php\">\r\n public function load($entity, $identifier, $arguments = [])\r\n    {\r\n        $entityType = $this->typeResolver->resolve($entity);\r\n        $operation = $this->operationPool->getOperation($entityType, \'read\');\r\n        if (!($operation instanceof ReadInterface)) {\r\n            throw new \\LogicException(get_class($operation) . \' must implement \' . ReadInterface::class);\r\n        }\r\n        $entity = $operation->execute($entity, $identifier, $arguments);\r\n        return $entity;\r\n    }\r\n</pre>\r\n\r\n\r\nIn the Operation/Read.php\r\n\r\n<pre lang=\"php\">\r\n  $entity = $this->readMain->execute($entity, $identifier);\r\n        $entityData = array_merge($hydrator->extract($entity), $arguments);\r\n        if (isset($entityData[$metadata->getLinkField()])) {\r\n            $entity = $this->readAttributes->execute($entity, $arguments);\r\n            $entity = $this->readExtensions->execute($entity, $arguments);\r\n        }\r\n</pre>\r\n\r\n\r\n\r\nIn ReadExtension .php\r\n<pre lang=\"php\">\r\n public function execute($entity, $arguments = [])\r\n    {\r\n        $entityType = $this->typeResolver->resolve($entity);\r\n        $actions = $this->extensionPool->getActions($entityType, \'read\');\r\n        foreach ($actions as $action) {\r\n                $entity = $action->execute($entity, $arguments);\r\n        }\r\n        return $entity;\r\n    }\r\n\r\n</pre>\r\n\r\n<img src=\"http://127.0.0.1/wordpress/wp1/wp-content/uploads/2018/09/ReadBundleExtension.png\" alt=\"\" width=\"2495\" height=\"1416\" class=\"alignnone size-full wp-image-133\" />\r\n\r\nnamespace Magento\\Bundle\\Model\\Product\\ReadHandler;\r\n\r\n<pre lang=\"php\">\r\n public function execute($entity, $arguments = [])\r\n    {\r\n        /** @var $entity \\Magento\\Catalog\\Api\\Data\\ProductInterface */\r\n        if ($entity->getTypeId() != \\Magento\\Bundle\\Model\\Product\\Type::TYPE_CODE) {\r\n            return $entity;\r\n        }\r\n        $entityExtension = $entity->getExtensionAttributes();\r\n        $options = $this->optionRepository->getListByProduct($entity);\r\n        if ($options) {\r\n            $entityExtension->setBundleProductOptions($options);\r\n        }\r\n        $entity->setExtensionAttributes($entityExtension);\r\n        return $entity;\r\n    }\r\n</pre>','Bundle product','','inherit','closed','closed','','124-revision-v1','','','2018-09-26 07:40:44','2018-09-26 07:40:44','',124,'http://127.0.0.1/wordpress/wp1/?p=137',0,'revision','',0),(138,1,'2018-09-26 07:55:23','2018-09-26 07:55:23','Controller -->  Model/Cart --> Quote -->   Product\\AbstractProductType   -> Each Product type is able to add more logic on this process\r\n\r\n\r\nQuote.php\r\n\r\n<pre lang=\"php\">\r\n\r\n        $cartCandidates = $product->getTypeInstance()->prepareForCartAdvanced($request, $product, $processMode);\r\n\r\n        /**\r\n         * Error message\r\n         */\r\n        if (is_string($cartCandidates) || $cartCandidates instanceof \\Magento\\Framework\\Phrase) {\r\n            return strval($cartCandidates);\r\n        }\r\n\r\n        /**\r\n         * If prepare process return one object\r\n         */\r\n        if (!is_array($cartCandidates)) {\r\n            $cartCandidates = [$cartCandidates];\r\n        }\r\n\r\n        $parentItem = null;\r\n        $errors = [];\r\n        $item = null;\r\n        $items = [];\r\n        foreach ($cartCandidates as $candidate) {\r\n            // Child items can be sticked together only within their parent\r\n            $stickWithinParent = $candidate->getParentProductId() ? $parentItem : null;\r\n            $candidate->setStickWithinParent($stickWithinParent);\r\n\r\n            $item = $this->getItemByProduct($candidate);\r\n            if (!$item) {\r\n                $item = $this->itemProcessor->init($candidate, $request);\r\n                $item->setQuote($this);\r\n                $item->setOptions($candidate->getCustomOptions());\r\n                $item->setProduct($candidate);\r\n                // Add only item that is not in quote already\r\n                $this->addItem($item);\r\n            }\r\n            $items[] = $item;\r\n\r\n            /**\r\n             * As parent item we should always use the item of first added product\r\n             */\r\n            if (!$parentItem) {\r\n                $parentItem = $item;\r\n            }\r\n            if ($parentItem && $candidate->getParentProductId() && !$item->getParentItem()) {\r\n                $item->setParentItem($parentItem);\r\n            }\r\n\r\n            $this->itemProcessor->prepare($item, $request, $candidate);\r\n\r\n            // collect errors instead of throwing first one\r\n            if ($item->getHasError()) {\r\n                foreach ($item->getMessage(false) as $message) {\r\n                    if (!in_array($message, $errors)) {\r\n                        // filter duplicate messages\r\n                        $errors[] = $message;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n        if (!empty($errors)) {\r\n            throw new \\Magento\\Framework\\Exception\\LocalizedException(__(implode(\"\\n\", $errors)));\r\n        }\r\n\r\n        $this->_eventManager->dispatch(\'sales_quote_product_add_after\', [\'items\' => $items]);\r\n        return $parentItem;\r\n</pre>\r\n\r\n\r\nTrong AbstractType\r\n<pre lang=\"php\">\r\n\r\n  */\r\n    public function prepareForCartAdvanced(\\Magento\\Framework\\DataObject $buyRequest, $product, $processMode = null)\r\n    {\r\n        if (!$processMode) {\r\n            $processMode = self::PROCESS_MODE_FULL;\r\n        }\r\n        $_products = $this->_prepareProduct($buyRequest, $product, $processMode);\r\n        $this->processFileQueue();\r\n        return $_products;\r\n    }\r\n\r\n\r\n /**\r\n     * Prepare product and its configuration to be added to some products list.\r\n     * Perform standard preparation process and then prepare options belonging to specific product type.\r\n     *\r\n     * @param  \\Magento\\Framework\\DataObject $buyRequest\r\n     * @param  \\Magento\\Catalog\\Model\\Product $product\r\n     * @param  string $processMode\r\n     * @return array|string\r\n     * @SuppressWarnings(PHPMD.CyclomaticComplexity)\r\n     */\r\n    protected function _prepareProduct(\\Magento\\Framework\\DataObject $buyRequest, $product, $processMode)\r\n    {\r\n        // try to add custom options\r\n        try {\r\n            $options = $this->_prepareOptions($buyRequest, $product, $processMode);\r\n        } catch (\\Magento\\Framework\\Exception\\LocalizedException $e) {\r\n            return $e->getMessage();\r\n        }\r\n\r\n        if (is_string($options)) {\r\n            return $options;\r\n        }\r\n        // try to found super product configuration\r\n        $superProductConfig = $buyRequest->getSuperProductConfig();\r\n        if (!empty($superProductConfig[\'product_id\']) && !empty($superProductConfig[\'product_type\'])) {\r\n            $superProductId = (int)$superProductConfig[\'product_id\'];\r\n            if ($superProductId) {\r\n                /** @var \\Magento\\Catalog\\Model\\Product $superProduct */\r\n                $superProduct = $this->_coreRegistry->registry(\'used_super_product_\' . $superProductId);\r\n                if (!$superProduct) {\r\n                    $superProduct = $this->productRepository->getById($superProductId);\r\n                    $this->_coreRegistry->register(\'used_super_product_\' . $superProductId, $superProduct);\r\n                }\r\n                $assocProductIds = $superProduct->getTypeInstance()->getAssociatedProductIds($superProduct);\r\n                if (in_array($product->getId(), $assocProductIds)) {\r\n                    $productType = $superProductConfig[\'product_type\'];\r\n                    $product->addCustomOption(\'product_type\', $productType, $superProduct);\r\n\r\n                    $buyRequest->setData(\r\n                        \'super_product_config\',\r\n                        [\'product_type\' => $productType, \'product_id\' => $superProduct->getId()]\r\n                    );\r\n                }\r\n            }\r\n        }\r\n\r\n        $product->prepareCustomOptions();\r\n        $buyRequest->unsetData(\'_processing_params\');\r\n        // One-time params only\r\n        $product->addCustomOption(\'info_buyRequest\', $this->serializer->serialize($buyRequest->getData()));\r\n        if ($options) {\r\n            $optionIds = array_keys($options);\r\n            $product->addCustomOption(\'option_ids\', implode(\',\', $optionIds));\r\n            foreach ($options as $optionId => $optionValue) {\r\n                $product->addCustomOption(self::OPTION_PREFIX . $optionId, $optionValue);\r\n            }\r\n        }\r\n\r\n        // set quantity in cart\r\n        if ($this->_isStrictProcessMode($processMode)) {\r\n            $product->setCartQty($buyRequest->getQty());\r\n        }\r\n        $product->setQty($buyRequest->getQty());\r\n\r\n        return [$product];\r\n  </pre>\r\n\r\n\r\nin product.php\r\n<pre lang=\"php\">\r\n  /**\r\n     * Prepare product custom options.\r\n     * To be sure that all product custom options does not has ID and has product instance\r\n     *\r\n     * @return \\Magento\\Catalog\\Model\\Product\r\n     */\r\n    public function prepareCustomOptions()\r\n    {\r\n     