ks()\r\n    {\r\n        $this->beforeGenerateBlock();\r\n\r\n        Profiler::start(\'LAYOUT\');\r\n        /* dispatch event for adding xml layout elements */\r\n        $this->eventManager->dispatch(\r\n            \'layout_generate_blocks_before\',\r\n            [\'full_action_name\' => $this->request->getFullActionName(), \'layout\' => $this->layout]\r\n        );\r\n        Profiler::start(\'layout_generate_blocks\');\r\n\r\n        /* generate blocks from xml layout */\r\n        $this->layout->generateElements();\r\n\r\n        Profiler::stop(\'layout_generate_blocks\');\r\n        $this->eventManager->dispatch(\r\n            \'layout_generate_blocks_after\',\r\n            [\'full_action_name\' => $this->request->getFullActionName(), \'layout\' => $this->layout]\r\n        );\r\n        Profiler::stop(\'LAYOUT\');\r\n\r\n        $this->afterGenerateBlock();\r\n\r\n        return $this;\r\n    }\r\n</pre>\r\n\r\n\r\nLayout.php\r\n\r\n<pre lang=\"php\">\r\n /**\r\n     * Create structure of elements from the loaded XML configuration\r\n     *\r\n     * @return void\r\n     */\r\n    public function generateElements()\r\n    {\r\n        \\Magento\\Framework\\Profiler::start(__CLASS__ . \'::\' . __METHOD__);\r\n        $cacheId = \'structure_\' . $this->getUpdate()->getCacheId();\r\n        $result = $this->cache->load($cacheId);\r\n        if ($result) {\r\n            $data = $this->serializer->unserialize($result);\r\n            $this->getReaderContext()->getPageConfigStructure()->populateWithArray($data[\'pageConfigStructure\']);\r\n            $this->getReaderContext()->getScheduledStructure()->populateWithArray($data[\'scheduledStructure\']);\r\n        } else {\r\n            \\Magento\\Framework\\Profiler::start(\'build_structure\');\r\n            $this->readerPool->interpret($this->getReaderContext(), $this->getNode());\r\n            \\Magento\\Framework\\Profiler::stop(\'build_structure\');\r\n\r\n            $data = [\r\n                \'pageConfigStructure\' => $this->getReaderContext()->getPageConfigStructure()->__toArray(),\r\n                \'scheduledStructure\'  => $this->getReaderContext()->getScheduledStructure()->__toArray(),\r\n            ];\r\n            $this->cache->save($this->serializer->serialize($data), $cacheId, $this->getUpdate()->getHandles());\r\n        }\r\n\r\n        $generatorContext = $this->generatorContextFactory->create(\r\n            [\r\n                \'structure\' => $this->structure,\r\n                \'layout\' => $this,\r\n            ]\r\n        );\r\n\r\n        \\Magento\\Framework\\Profiler::start(\'generate_elements\');\r\n        $this->generatorPool->process($this->getReaderContext(), $generatorContext);\r\n        \\Magento\\Framework\\Profiler::stop(\'generate_elements\');\r\n\r\n        $this->addToOutputRootContainers();\r\n        \\Magento\\Framework\\Profiler::stop(__CLASS__ . \'::\' . __METHOD__);\r\n    }\r\n</pre>\r\n\r\n\r\n\r\nIn the updates property of layout.php\r\n<pre lang=\"xml\">\r\n<body>\r\n   <referenceContainer name=\"content\">\r\n      <uiComponent name=\"customer_listing\"/>\r\n   </referenceContainer>\r\n</body>\r\n</pre>\r\n\r\n\r\nin \\Layout\\Generato\\rContext\r\nThere is elements property you can used to see result\r\n\r\n\r\nLayout\\GeneratorPool\r\n\r\neach element of pooll\r\n->process\r\n<img src=\"http://127.0.0.1/wordpress/wp1/wp-content/uploads/2018/09/if_column_has_editor.png\" alt=\"\" width=\"2495\" height=\"1416\" class=\"alignnone size-full wp-image-158\" />\r\n\r\n<img src=\"http://127.0.0.1/wordpress/wp1/wp-content/uploads/2018/09/after_run_prepareComponent.png\" alt=\"\" width=\"2495\" height=\"1416\" class=\"alignnone size-full wp-image-159\" />\r\n\r\n<img src=\"http://127.0.0.1/wordpress/wp1/wp-content/uploads/2018/09/Datasource.png\" alt=\"\" width=\"2495\" height=\"1416\" class=\"alignnone size-full wp-image-160\" />\r\n\r\n<img src=\"http://127.0.0.1/wordpress/wp1/wp-content/uploads/2018/09/uicomponent