$(document).ready(function(){
$('#selectAll').click(function() {
$('.chkselect').prop('checked', this.checked);});
$(this).bind("contextmenu", function(e) {e.preventDefault();});
});

function show_modal(target)
{
    waitingDialog.show();
    target=target;
    $('#targetModal').load(target, function() {
        $('#targetModal').modal('show');
        waitingDialog.hide();
    });
}
$(document).ready(function(){
    $('.multiselectgrp').multiselect({includeSelectAllOption: true,maxHeight: 300,
    allSelectedText: 'All Selected',filterPlaceholder: 'Search',nonSelectedText: 'None selected',enableFiltering: true,enableCaseInsensitiveFiltering: true});
});